<?php/** * @package		Magento SOAP v1 API Wrapper (Laravel Bundle) * @author		Matteo Castiglioni <http://www.rabbitpills.com> * @version		Release: 1.0 */class Magentoapi_Home_Controller extends Base_Controller{	public $restful = true;		private $_mapi_instance;		public function __construct()	{        parent::__construct();				$this->_mapi_instance = new Mapi();    }		public function get_index()	{		try{			$wsdl_methods = $this->_mapi_instance->get_resources();			return View::make('magentoapi::home.index')				->with('title','Magento SOAP API')				->with('pagetitle','Magento SOAP API Methods')				->with('apilist', json_decode(Response::json($wsdl_methods)));		}catch(Exception $exception){			echo $exception->getMessage();		}	}	public function get_exec_call($method=NULL, $params=NULL)	{		try{			return $this->_mapi_instance->call($method, $params);		}catch(Exception $exception){			echo $exception->getMessage();		}	}	public function post_exec_call($method=NULL, $params=NULL)	{		try{			return $this->_mapi_instance->call($method, $params);		}catch(Exception $exception){			echo $exception->getMessage();		}	}	    /**     * Catch-all method for requests that can't be matched.     *     * @param  string    $method     * @param  array     $parameters     * @return Response     */    public function __call($method, $parameters)	{        return Response::error('404');    }}